import React from "react";

const ShowTeacher = (props) => {
  const { id, name, designation, technology, email, phone, salary, img } =
    props.teacher;
  return (
    <div className="teacher">
      <div className="">
        <h3>name: {name}</h3>
        <button
          onClick={() => props.handleAddToChart(props.product)}
          className="btn-regular"
        >
          {cartIcon}Add To Cart
        </button>
      </div>
    </div>
  );
};

export default ShowTeacher;
