import React from "react";
import "./Cart.css";

const Cart = (props) => {
  const { cart } = props;
  let total = 0;
  for (const teacher of cart) {
    total = total + teacher.salary;
  }
  return (
    <div>
      <h3 className="text-center">Teachers</h3>
      <hr />
      <h5>Teachers Added: {props.cart.length}</h5>
      <h5>Total Cost: ${total.toFixed(2)}</h5>
      <button className="btn btn-success">View Details</button>
      {/* <h3>Name:{props.cart.name}</h3> */}
    </div>
  );
};

export default Cart;
